import React from 'react';
import { Link } from 'react-router-dom';
import AdminHeader from '../Admin/AdminHeader';
import AdminSidebar from '../Admin/AdminSidebar';

const Courselist = () => {
  const courses = [
    { id: 1, name: "Web Development",   thumbnail: "data:image/jpeg;base64," },
    { id: 2, name: "Web Development",  thumbnail: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRXqOfuqYDP2rusWqKjpT9yHF7YH6P3edwQyQ&s" },
    // { id: 3, name: "Graphic Designing", thumbnail: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcREJyqTDZF5Rl2xPbXKhxeVIeOtdENL4j2iyg&s" },
  ];

  return (
    <div className='course-list-adminpannel'>
      <AdminHeader />
      <AdminSidebar />
      <div className="course-container px-3">
      <div className='addcource-btn-parent'>
       <Link to='/addcourse' className='add-course'>Add New Course ↓</Link>
         </div>      
      <div className="course-list flex justify-center gap-3 ">
        {courses.map((course) => (
          <div key={course.id} className="admin-course-card">
            <img  src={course.thumbnail} alt={course.name} />
            <h4 className='mt-5 mb-7' > {course.name}</h4>
            {/* <h5 className='mt-1 mb-3'><span className='font-bold'>Batch: </span> {course.batch}</h5> */}
            <Link to={`/batch-list/${course.id}`} className='batch-list-btn border px-4 py-3 mt-3  text-white '>View Course</Link>
          </div>
        ))}
      </div>
      </div>

    </div>
  );
};

export default Courselist;



